function ft_merito_semestre(p_id_persona in integer,p_id_programa_estudio in integer,p_id_semestre in integer,p_ciclo in integer) 
return number
is
  p_num number;
begin 
    
    select puesto into p_num
    from (select rownum puesto,a.id_persona,a.credito_ciclo
          from (
                select aac.id_persona,Aac.Id_Plan_Programa,aac.ciclo,
                Ft_Ponderado_Ciclo(aac.id_persona,aac.id_semestre,aac.id_nivel_ensenanza) as credito_ciclo
                from Vw_Acad_Alumno_Contrato aac
                where aac.id_semestre=p_id_semestre and aac.estado='1' and Aac.Id_Modo_Contrato=1 
                and Aac.Id_Programa_Estudio=p_id_programa_estudio and aac.ciclo=p_ciclo
                order by credito_ciclo desc) a)
    where id_persona=p_id_persona;
    
    return p_num;
    
end;

