function ft_nivel_rieso_alumno(p_nota in number) return varchar2 is
  v_res varchar2(20);
  begin
  
    select (case when nota<10 then 'MA' 
                  when nota Between 10 and 12 then 'A' 
                  when nota Between 13 and 14 then 'M' 
                  when nota Between 15 and 16 then 'B' 
                  when nota >16 then 'MB' end ) into v_res
    from (select p_nota nota from dual);
    
    return v_res;
  end;

