function ft_num_derivaciones_ciclo_eap(v_id_semestre number,v_id_adviser_derivacion number , v_id_programa_estudio number)

return number

is



    num_estudiantes number;

    

begin



    select count(id_persona) into num_estudiantes

    from(select Distinct adt.id_persona 

          from Adviser_Sesion_Derivacion asd 
          inner join Adviser_Sesion ads on Ads.Id_Adviser_Sesion=Asd.Id_Adviser_Sesion

          inner join adviser_tutorando adt on (Adt.Id_Adviser_Tutorando=Ads.Id_Adviser_Tutorando )
        --  OR (adt.id_persona=ads.id_persona AND ads.id_semestre=adt.id_semestre)
          inner join acad_Plan_Programa app on App.Id_Plan_Programa=Adt.Id_Plan_Programa

          inner join Acad_Programa_Estudio ape on Ape.Id_Programa_Estudio=App.Id_Programa_Estudio

          where Ads.Id_Semestre=v_id_semestre

          and Asd.Id_Adviser_Derivacion=v_id_adviser_derivacion

          and Ape.Id_Programa_Estudio=v_id_programa_estudio);

    

    return num_estudiantes;



end;

