SET client_encoding TO 'UTF8';

SET search_path = david,public;
\set ON_ERROR_STOP ON

FUNCTION ft_num_horas_curso( p_id_carga_curso integer) RETURN number IS
   horario_b varchar2(4000);
   horas number(30);
   cursor curso is
   		  select horario
        from acad_carga_curso_docente
        where id_carga_curso=p_id_carga_curso;

BEGIN

	 horas := 0;

	 open curso;
	 fetch curso into horario_b ;

	 while curso%found loop
	 	   for i in 1..150 loop       

			   if ( substr(horario_b,i,1) = '1') then
			   	  horas := horas + 1;
			   end if;

		   end loop;
       
	   	 fetch curso into horario_b;
       
	 end loop;

	 close curso;
	 RETURN( horas );
END;

