SET client_encoding TO 'UTF8';

SET search_path = david,public;
\set ON_ERROR_STOP ON

function       ft_n_ponderado_general_plan(p_id_persona in integer,p_id_plan_programa in integer) 
return number
is
  p_plan number;
  p_area number;
  p_anio number;
  p_promedio number;
begin  

  select Id_Plan,Id_Escuela,substr(Nombre_Plan,0,4) into p_plan,p_area,p_anio from Vw_Plan_Programa where id_plan_programa=p_id_plan_programa;

  select round((sum(promedio*credito)/decode(sum(credito),0,1,sum(credito))),2) into p_promedio
  from (select distinct acp.id_curso_detalle,aca.ID_CURSO_ALUMNO,
                ACP.ID_PLAN_CURSO,acp.ciclo,ACP.CURSO as nombre,
                aca.promedio,aca.id_tipo_condicion,acp.credito--,Accd.Fecha_Fin
        from VW_PLAN_PROGRAMA vpp 
        inner join ACAD_PLAN_CURSO apc on VPP.ID_PLAN=apc.ID_PLAN
        inner join acad_curso_alumno aca on aca.Id_Plan_Programa=VPP.ID_PLAN_PROGRAMA and aca.id_persona=p_id_persona 
                and aca.Id_Plan_Curso=apc.Id_Plan_Curso
                and aca.estado='1' and aca.Id_Tipo_Condicion not in (3,7,15)
        inner join VW_ACAD_CURSO_PLAN acp on acp.id_plan_curso=decode(apc.Id_Plan_Curso_Electivo,null,apc.Id_Plan_Curso,apc.Id_Plan_Curso_Electivo)
        where VPP.ID_PLAN_PROGRAMA=p_id_plan_programa and acp.ID_PLAN_CURSO_ELECTIVO is null
        
		UNION
        
        -- Query para obtener los cursos obligatorios del mismo programa en la carga
        SELECT DISTINCT acp.id_curso_detalle, aca.ID_CURSO_ALUMNO, aca.ID_PLAN_CURSO, acp.ciclo, ACP.CURSO AS nombre, aca.promedio, aca.id_tipo_condicion, acp.credito
        FROM david.ACAD_CURSO_ALUMNO aca
        INNER JOIN david.VW_ACAD_CARGA_ACADEMICA vaca ON vaca.ID_CARGA_CURSO = aca.ID_CARGA_CURSO AND vaca.ORIGEN = 'O'
        INNER JOIN david.VW_ACAD_CURSO_PLAN acp ON acp.id_plan_curso = aca.ID_PLAN_CURSO
        INNER JOIN david.ACAD_CARGA_CURSO acc ON acc.ID_CARGA_CURSO = vaca.ID_CARGA_CURSO
        WHERE aca.ID_PERSONA = p_id_persona
        AND vaca.ID_CURSO_MODO = 8
        AND aca.ID_PLAN_CURSO NOT IN (
        	SELECT apc.ID_PLAN_CURSO 
	        FROM david.VW_PLAN_PROGRAMA vpp 
	        INNER JOIN david.ACAD_PLAN_CURSO apc ON apc.ID_PLAN = vpp.ID_PLAN 
	        WHERE vpp.ID_PLAN_PROGRAMA = p_id_plan_programa
        )
        AND aca.estado='1' 
        AND aca.Id_Tipo_Condicion NOT IN (3,7,15)
        AND vaca.ID_PROGRAMA_ESTUDIO IN (
        	SELECT app.ID_PROGRAMA_ESTUDIO
        	FROM david.ACAD_PLAN_PROGRAMA app 
        	WHERE app.ID_PLAN_PROGRAMA = p_id_plan_programa
        )
        
        UNION
        
        -- Query para obtener los cursos obligatorios de otros programas en la carga
        SELECT acp.id_curso_detalle, aca.ID_CURSO_ALUMNO, aca.ID_PLAN_CURSO, acp.ciclo, ACP.CURSO AS nombre, aca.promedio, aca.id_tipo_condicion, acp.credito
        FROM david.ACAD_CURSO_ALUMNO aca
        INNER JOIN david.VW_ACAD_CARGA_ACADEMICA vaca ON vaca.ID_CARGA_CURSO = aca.ID_CARGA_CURSO AND vaca.ORIGEN = 'O'
        INNER JOIN david.VW_ACAD_CURSO_PLAN acp ON acp.id_plan_curso = aca.ID_PLAN_CURSO
        INNER JOIN david.ACAD_CARGA_CURSO acc ON acc.ID_CARGA_CURSO = vaca.ID_CARGA_CURSO
        WHERE aca.ID_PERSONA = p_id_persona
        AND vaca.ID_CURSO_MODO = 8
        AND aca.ID_PLAN_CURSO NOT IN (
        	SELECT apc.ID_PLAN_CURSO 
	        FROM david.VW_PLAN_PROGRAMA vpp 
	        INNER JOIN david.ACAD_PLAN_CURSO apc ON apc.ID_PLAN = vpp.ID_PLAN 
	        WHERE vpp.ID_PLAN_PROGRAMA = p_id_plan_programa
        )
        AND aca.estado='1' 
        AND aca.Id_Tipo_Condicion NOT IN (3,7,15)
        AND vaca.ID_PROGRAMA_ESTUDIO IN (
        	SELECT DISTINCT ape2.ID_PROGRAMA_ESTUDIO 
            FROM david.VW_ACAD_CARGA_ACADEMICA vaca 
            INNER JOIN david.ACAD_CURSO_MODO acm ON acm.ID_CURSO_MODO = vaca.ID_CURSO_MODO
            INNER JOIN david.ACAD_PROGRAMA_ESTUDIO ape2 ON ape2.ID_PROGRAMA_ESTUDIO = vaca.ID_PROGRAMA_ESTUDIO
            WHERE acm.codigo = 'L-31803'
            AND vaca.ORIGEN = 'O'
            AND vaca.ID_SEDE IN (
                SELECT osa.ID_SEDE FROM david.ACAD_PLAN_PROGRAMA app 
		        INNER JOIN david.ACAD_PROGRAMA_ESTUDIO ape ON ape.ID_PROGRAMA_ESTUDIO = app.ID_PROGRAMA_ESTUDIO 
		        INNER JOIN eliseo.ORG_SEDE_AREA osa ON osa.ID_SEDEAREA = ape.ID_SEDEAREA 
		        WHERE app.ID_PLAN_PROGRAMA = p_id_plan_programa
            )
            AND ape2.ID_PROGRAMA_ESTUDIO NOT IN (
	            SELECT app5.ID_PROGRAMA_ESTUDIO
	        	FROM david.ACAD_PLAN_PROGRAMA app5 
	        	WHERE app5.ID_PLAN_PROGRAMA = p_id_plan_programa
        	)
        )
        
       order by ciclo,nombre);

  return p_promedio;
end;

