FUNCTION ft_observacion_adviser(
    p_id_adviser_sesion VARCHAR2) 
RETURN varchar2
aS
    r_texto varchar2(5000);
BEGIN

	
    SELECT substr(detalle,1,50) ||'...'  INTO r_texto FROM adviser_sesion_Acad sa
    INNER JOIN adviser_bloque_Det ab ON sa.ID_ADVISER_BLOQUE_DET =ab.ID_ADVISER_BLOQUE_DET
    WHERE ab.codigo='2020002'
    AND sa.id_adviser_sesion=p_id_adviser_sesion
    AND rownum=1;
    
    

    RETURN r_texto;
END;

