SET client_encoding TO 'UTF8';

SET search_path = david,public;
\set ON_ERROR_STOP ON

FUNCTION       FT_PERMISO_AVISO(
    ID_PERMISO_P IN NUMBER
) RETURN VARCHAR2 AS
    RESULTADO VARCHAR2(100);
BEGIN
    SELECT (CASE
                WHEN (SYSDATE BETWEEN RP.FECHA_HINICIO AND (RP.FECHA_HFIN + (NVL(RP.HORA_EXTENSION, 0) / 24) +
                                                            (NVL(RP.MINUTO_EXTENSION, 0) / 1440)))
                    THEN (
                    CASE
                        WHEN SYSDATE >= ((RP.FECHA_HFIN + (NVL(RP.HORA_EXTENSION, 0) / 24) +
                                          (NVL(RP.MINUTO_EXTENSION, 0) / 1440)) - 15 / 1440)
                            THEN (
                            CASE
                                WHEN SYSDATE >= ((RP.FECHA_HFIN + (NVL(RP.HORA_EXTENSION, 0) / 24) +
                                                  (NVL(RP.MINUTO_EXTENSION, 0) / 1440)) - 5 / 1440)
                                    THEN 'Falta menos de 5 minutos para vencer'
                                ELSE 'Falta menos de 15 minutos para vencer' END
                            )

                        ELSE 'Esta a tiempo' END
                    )
                ELSE 'Permiso vencido' END)
    INTO RESULTADO
    FROM DAVID.RESIDENCIA_PERMISO RP
    WHERE RP.ID_PERMISO = ID_PERMISO_P;

    RETURN RESULTADO;

END;

