SET client_encoding TO 'UTF8';

SET search_path = david,public;
\set ON_ERROR_STOP ON

FUNCTION       FT_PLANP_ALUMNO_NAME(P_ID_PERSONA IN INTEGER) 
RETURN VARCHAR2
IS
  P_PLAN_PROGRAMA VARCHAR2(200);
BEGIN
    SELECT DISTINCT VPP.NOM_ESCUELA INTO P_PLAN_PROGRAMA
    FROM DAVID.VW_ALUMNO_PLAN_PROGRAMA VPP 
    WHERE  ID_PERSONA=P_ID_PERSONA
    AND ID_PLAN_PROGRAMA = (SELECT  ID_PLAN_PROGRAMA
                            FROM (SELECT ID_PLAN_PROGRAMA,ID_PERSONA FROM DAVID.VW_ALUMNO_PLAN_PROGRAMA WHERE ESTADO=1 ORDER BY ID_FACULTAD)
                            WHERE ID_PERSONA=VPP.ID_PERSONA AND ROWNUM=1);
    RETURN P_PLAN_PROGRAMA;
END;

