SET client_encoding TO 'UTF8';

SET search_path = david,public;
\set ON_ERROR_STOP ON

function ft_ponderado_ciclo_escuela(p_id_persona in integer,p_id_escuela in integer,p_id_semestre in integer) 

return number

is

  p_promedio number;

begin  



  select nvl((select round(sum(nvl(aca.Promedio,0)*aca.Credito)/decode(sum(aca.Credito),0,1,sum(aca.Credito)),2)
  from Vw_Acad_Curso_Alumno  aca 
  where aca.id_persona=p_id_persona
  and aca.id_semestre=p_id_semestre
  and aca.estado='1' and aca.id_tipo_condicion in (1,2,9,13,17)-- and aca.cerrado='S'
  and Aca.Id_escuela=p_id_escuela),0) into p_promedio
  from dual;

  

  return p_promedio;

  

end;

