function ft_ponderado_general_plan(p_id_persona in integer,p_id_plan_programa in integer) 
return number
is
  p_plan number;
  p_area number;
  p_anio number;
  p_promedio number;
begin  

  select Id_Plan,Id_Escuela,substr(Nombre_Plan,0,4) into p_plan,p_area,p_anio from Vw_Plan_Programa where id_plan_programa=p_id_plan_programa;
  
    select round((sum(promedio*credito)/decode(sum(credito),0,1,sum(credito))),2) into p_promedio
      from (select distinct apc.id_curso_alumno,acp.id_curso_detalle,apc.id_carga_curso,ACP.ID_PLAN_CURSO,acp.ciclo,ACP.CURSO as nombre,
            apc.promedio,apc.id_tipo_condicion,acp.credito,Accd.Fecha_Fin
            from VW_PLAN_PROGRAMA vpp inner join VW_ACAD_CURSO_PLAN acp on VPP.ID_PLAN=ACP.ID_PLAN
            inner join acad_curso_alumno apc on Apc.Id_Plan_Programa=Vpp.Id_Plan_Programa and apc.id_persona=p_id_persona 
                  and apc.Id_Plan_Curso=decode(acp.Id_Plan_Curso_Electivo,null,acp.Id_Plan_Curso,acp.Id_Plan_Curso_Electivo)
                  and apc.estado='1' and Apc.Id_Tipo_Condicion not in (3,7,15)
            left join (select accd.id_carga_curso,max(amd.fecha_fin) fecha_fin
                      from acad_carga_curso_docente accd inner join acad_modulo_detalle amd on amd.id_modulo_detalle=accd.id_modulo_detalle
                      group by accd.id_carga_curso) accd on accd.id_carga_curso=apc.id_carga_curso
            where VPP.ID_PLAN_PROGRAMA=p_id_plan_programa and acp.ID_PLAN_CURSO_ELECTIVO is null
            order by ciclo,ACP.CURSO);
  
  return p_promedio;
  
end;

