function ft_ponderado_general_prog_s(p_id_persona in integer,p_id_plan_programa in integer,p_id_semestre in integer) 
return number
is
  p_promedio number;
  p_semestre varchar2(400);
  p_semestre_ant varchar2(400);
begin  

  select semestre,Semestre_Ant into p_semestre,p_semestre_ant from acad_semestre where id_semestre=p_id_semestre;

  select nvl((select round(sum(nvl(aca.Promedio,0)*acd.Credito)/decode(sum(acd.Credito),0,1,sum(acd.Credito)),2)
  from Acad_Curso_Alumno aca inner join Acad_Plan_Curso apc on Apc.Id_Plan_Curso=aca.Id_Plan_Curso
  left join acad_carga_curso acc on acc.id_carga_curso=aca.id_carga_curso
  left join acad_semestre_programa asp on Asp.Id_Semestre_Programa=Acc.Id_Semestre_Programa
  left join acad_semestre asm on asm.id_semestre=Asp.Id_Semestre
  left join Acad_Plan_Curso apce on Apce.Id_Plan_Curso=Apc.Id_Plan_Curso_Electivo
  inner join acad_curso_detalle acd on Acd.Id_Curso_Detalle=nvl(apce.id_curso_detalle,apc.id_curso_detalle)
  where aca.id_persona=p_id_persona	
  and aca.Id_Plan_Programa=p_id_plan_programa
  and replace(nvl(asm.semestre,p_semestre_ant),'M','2')<replace(p_semestre,'M','2')
  and aca.estado='1' and id_tipo_condicion in ('1','2')),0) into p_promedio
  from dual;
  
  return p_promedio;
  
end;

