SET client_encoding TO 'UTF8';

SET search_path = david,public;
\set ON_ERROR_STOP ON

function ft_ponderado_general_prueba(p_id_persona in integer,p_id_plan_programa in integer) 
return number
is
  p_plan number;
  p_area number;
  p_anio number;
  p_promedio number;
begin  

  select Id_Plan,Id_Escuela,substr(Nombre_Plan,0,4) into p_plan,p_area,p_anio from Vw_Plan_Programa where id_plan_programa=p_id_plan_programa;
  
  IF ((p_area = 163 or p_area=86 ) and p_anio <= 2011) THEN
  
    select round((sum(promedio*credito)/decode(sum(credito),0,1,sum(credito))),2) into p_promedio
      from (select distinct acp.id_curso_detalle,apc.id_carga_curso,ACP.ID_PLAN_CURSO,acp.ciclo,ACP.CURSO as nombre,
            apc.promedio,apc.id_tipo_condicion,acp.credito,Accd.Fecha_Fin
            from VW_PLAN_PROGRAMA vpp inner join VW_ACAD_CURSO_PLAN acp on VPP.ID_PLAN=ACP.ID_PLAN
            inner join (
                        select                     
                        apc.id_curso_detalle,aca.ID_PLAN_CURSO,aca.promedio,
                        ACA.FECHA_REGISTRO,ACA.ID_CARGA_CURSO,aca.id_tipo_condicion
                        from vw_acad_curso_plan apc 
                        inner join VW_CURSO_EQUIVALENTE vce on VCE.ID_CURSO_DETALLE=apc.ID_CURSO_DETALLE
                        inner join ACAD_CURSO_DETALLE acd on ACD.ID_CURSO_DETALLE=VCE.CURSO_EQUIV and acd.id_area in (8,10)
                        inner join acad_curso_alumno aca on ACA.ID_CURSO_DETALLE=VCE.CURSO_EQUIV and aca.id_persona=p_id_persona and aca.estado='1'
                        where apc.id_plan=p_plan and apc.ID_PLAN_CURSO_ELECTIVO is null
                        union
                        select                    
                        apc.id_curso_detalle,aca.ID_PLAN_CURSO,aca.promedio,
                        ACA.FECHA_REGISTRO,ACA.ID_CARGA_CURSO,aca.id_tipo_condicion
                        from VW_ACAD_CURSO_PLAN apce inner join acad_plan_curso apc 
                        on APC.ID_PLAN_CURSO=APCE.ID_PLAN_CURSO_ELECTIVO
                        inner join VW_CURSO_EQUIVALENTE vce on VCE.ID_CURSO_DETALLE=apce.ID_CURSO_DETALLE
                        inner join ACAD_CURSO_DETALLE acd on ACD.ID_CURSO_DETALLE=VCE.CURSO_EQUIV and acd.id_area in (8,10)
                        inner join acad_curso_alumno aca on ACA.ID_CURSO_DETALLE=VCE.CURSO_EQUIV and aca.id_persona=p_id_persona and aca.estado='1'
                        where apce.id_plan=p_plan and apce.ID_PLAN_CURSO_ELECTIVO is not null
                        ) apc on APC.ID_CURSO_DETALLE=ACP.ID_CURSO_DETALLE
            left join (select accd.id_carga_curso,max(amd.fecha_fin) fecha_fin
                      from acad_carga_curso_docente accd inner join acad_modulo_detalle amd on amd.id_modulo_detalle=accd.id_modulo_detalle
                      group by accd.id_carga_curso) accd on accd.id_carga_curso=apc.id_carga_curso
            where VPP.ID_PLAN_PROGRAMA=p_id_plan_programa and acp.ID_PLAN_CURSO_ELECTIVO is null
            order by ciclo,ACP.CURSO);
  
  ELSE
  
    select round((sum(promedio*credito)/decode(sum(credito),0,1,sum(credito))),2) into p_promedio
      from (select distinct acp.id_curso_detalle,apc.id_carga_curso,ACP.ID_PLAN_CURSO,acp.ciclo,ACP.CURSO as nombre,
            apc.promedio,apc.id_tipo_condicion,acp.credito,Accd.Fecha_Fin
            from VW_PLAN_PROGRAMA vpp inner join VW_ACAD_CURSO_PLAN acp on VPP.ID_PLAN=ACP.ID_PLAN
            inner join (
                        select                     
                        apc.id_curso_detalle,aca.ID_PLAN_CURSO,aca.promedio,
                        ACA.FECHA_REGISTRO,ACA.ID_CARGA_CURSO,aca.id_tipo_condicion
                        from vw_acad_curso_plan apc 
                        inner join VW_CURSO_EQUIVALENTE vce on VCE.ID_CURSO_DETALLE=apc.ID_CURSO_DETALLE
                        inner join ACAD_CURSO_DETALLE acd on ACD.ID_CURSO_DETALLE=VCE.CURSO_EQUIV and acd.id_area = apc.id_parent
                        inner join acad_curso_alumno aca on ACA.ID_CURSO_DETALLE=VCE.CURSO_EQUIV and aca.id_persona=p_id_persona and aca.estado='1'
                        where apc.id_plan=p_plan and apc.ID_PLAN_CURSO_ELECTIVO is null
                        union
                        select                    
                        apc.id_curso_detalle,aca.ID_PLAN_CURSO,aca.promedio,
                        ACA.FECHA_REGISTRO,ACA.ID_CARGA_CURSO,aca.id_tipo_condicion
                        from VW_ACAD_CURSO_PLAN apce inner join acad_plan_curso apc 
                        on APC.ID_PLAN_CURSO=APCE.ID_PLAN_CURSO_ELECTIVO
                        inner join VW_CURSO_EQUIVALENTE vce on VCE.ID_CURSO_DETALLE=apce.ID_CURSO_DETALLE
                        inner join ACAD_CURSO_DETALLE acd on ACD.ID_CURSO_DETALLE=VCE.CURSO_EQUIV and acd.id_area = apce.id_parent
                        inner join acad_curso_alumno aca on ACA.ID_CURSO_DETALLE=VCE.CURSO_EQUIV and aca.id_persona=p_id_persona and aca.estado='1'
                        where apce.id_plan=p_plan and apce.ID_PLAN_CURSO_ELECTIVO is not null
                        ) apc on APC.ID_CURSO_DETALLE=ACP.ID_CURSO_DETALLE
            left join (select accd.id_carga_curso,max(amd.fecha_fin) fecha_fin
                      from acad_carga_curso_docente accd inner join acad_modulo_detalle amd on amd.id_modulo_detalle=accd.id_modulo_detalle
                      group by accd.id_carga_curso) accd on accd.id_carga_curso=apc.id_carga_curso
            where VPP.ID_PLAN_PROGRAMA=p_id_plan_programa and acp.ID_PLAN_CURSO_ELECTIVO is null
            order by ciclo,ACP.CURSO);
  
  END IF;
  
  return p_promedio;
  
end;

