SET client_encoding TO 'UTF8';

SET search_path = david,public;
\set ON_ERROR_STOP ON

function ft_ponderado_general(p_id_persona in integer,p_id_semestre in integer,p_id_programa_estudio in integer) 
return number
is
  p_promedio number;
begin  

  select round(sum(nvl(aca.Promedio,0)*acd.Credito)/decode(sum(acd.Credito),0,1,sum(acd.Credito)),2) into p_promedio
  from Acad_Curso_Alumno aca inner join Vw_Plan_Programa vpp on Vpp.Id_Plan_Programa=Aca.Id_Plan_Programa
  inner join acad_curso_detalle acd on Acd.Id_Curso_Detalle=Aca.Id_Curso_Detalle
  where aca.id_persona=p_id_persona
  and Vpp.Id_Programa_Estudio=p_id_programa_estudio
  and aca.estado='1' and id_tipo_condicion in ('1','2');
  
  return p_promedio;
  
end;

