function ft_ponderado_plan(p_id_persona in integer,p_id_plan_programa in integer) 
return number
is
  p_plan number;
  res number;
begin  

  select Id_Plan into p_plan from Vw_Plan_Programa where id_plan_programa=p_id_plan_programa;
  
  select round(sum(promedio*credito)/sum(credito),2)  
  into res
  from (
  select APC.ID_CURSO_ALUMNO,apc.id_carga_curso,acp.id_curso_detalle,ACP.ID_PLAN_CURSO,apc.promedio,acp.credito
  from VW_PLAN_PROGRAMA vpp inner join VW_ACAD_CURSO_PLAN acp on VPP.ID_PLAN=ACP.ID_PLAN
  left join (
              select ACA.ID_CURSO_ALUMNO,apc.id_curso_detalle,aca.ID_PLAN_CURSO,aca.promedio,ACA.ID_CARGA_CURSO
              from vw_acad_curso_plan apc 
              inner join VW_CURSO_EQUIVALENTE vce on VCE.ID_CURSO_DETALLE=apc.ID_CURSO_DETALLE
              inner join ACAD_CURSO_DETALLE acd on ACD.ID_CURSO_DETALLE=VCE.CURSO_EQUIV and ACD.ID_AREA=APC.ID_PARENT
              inner join acad_curso_alumno aca on ACA.ID_CURSO_DETALLE=VCE.CURSO_EQUIV and aca.id_persona=p_id_persona and aca.estado='1'
              where apc.id_plan=p_plan and apc.ID_PLAN_CURSO_ELECTIVO is null and aca.id_tipo_condicion not in (7)
              union
              select ACA.ID_CURSO_ALUMNO,apc.id_curso_detalle,aca.ID_PLAN_CURSO,aca.promedio,ACA.ID_CARGA_CURSO
              from VW_ACAD_CURSO_PLAN apce inner join acad_plan_curso apc on APC.ID_PLAN_CURSO=APCE.ID_PLAN_CURSO_ELECTIVO
              inner join VW_CURSO_EQUIVALENTE vce on VCE.ID_CURSO_DETALLE=apce.ID_CURSO_DETALLE
              inner join ACAD_CURSO_DETALLE acd on ACD.ID_CURSO_DETALLE=VCE.CURSO_EQUIV and ACD.ID_AREA=APCE.ID_PARENT
              inner join acad_curso_alumno aca on ACA.ID_CURSO_DETALLE=VCE.CURSO_EQUIV and aca.id_persona=p_id_persona and aca.estado='1'
              where apce.id_plan=p_plan and apce.ID_PLAN_CURSO_ELECTIVO is not null and aca.id_tipo_condicion not in (7)) apc on APC.ID_CURSO_DETALLE=ACP.ID_CURSO_DETALLE
  where VPP.ID_PLAN_PROGRAMA=p_id_plan_programa and acp.ID_PLAN_CURSO_ELECTIVO is null);
  
  return res;
  
end;

