SET client_encoding TO 'UTF8';

SET search_path = david,public;
\set ON_ERROR_STOP ON

function ft_posgrado_persona(v_id_persona integer,v_semestre in varchar2) 
return varchar2
is
  p_escuela varchar2(4000);
begin  
  select Nom_Escuela into p_escuela
  from (select distinct 2 dato,decode(Vpp.Id_Tipo_Contrato,3,6,Vpp.Id_Tipo_Contrato),Vpp.Nom_Escuela 
        from moises.persona_natural_egresado pne inner join Vw_Plan_Programa vpp on Vpp.Id_Plan_Programa=Pne.Id_Plan_Programa
        where id_persona=v_id_persona and Vpp.Id_Tipo_Contrato in (4,5,3,7,17)
        union
        select 1 dato,decode(Id_Tipo_Contrato,3,6,Id_Tipo_Contrato),Nombre_Escuela from Vw_Acad_Alumno_Contrato 
        where id_persona=v_id_persona and Id_Tipo_Contrato in (4,5,3,7,17) and semestre=v_semestre and estado='1'
        order by 1,2)
  where rownum=1;
  return p_escuela;
end;

