FUNCTION FT_PRIMERA_MATRICULA_ALUMNOF( V_ID_PERSONA IN NUMBER, V_ID_PLAN_PROGRAMA IN NUMBER,V_SEMESTRE IN VARCHAR2) 
RETURN DATE 
IS 
p_escuela integer;
p_semestre DATE;
BEGIN

  SELECT ID_ESCUELA INTO p_escuela FROM VW_PLAN_PROGRAMA WHERE ID_PLAN_PROGRAMA=V_ID_PLAN_PROGRAMA;
  
  SELECT (CASE WHEN MIN(NVL(Aac.Fecha_Actualizacion,Aac.Fecha_Registro))<MIN(FA.Fecha_Actualizacion) THEN MIN(NVL(Aac.Fecha_Actualizacion,Aac.Fecha_Registro))
          ELSE MIN(FA.Fecha_Actualizacion) END) INTO p_semestre
  FROM VW_ACAD_ALUMNO_CONTRATO AAC INNER JOIN VW_ACAD_PROGRAMA_ESTUDIO APE ON APE.ID_PROGRAMA_ESTUDIO=AAC.ID_PROGRAMA_ESTUDIO
  left join (select aca.id_persona,aca.Id_Plan_Programa,asp.id_semestre,
                    max(nvl(Aca.Fecha_Actualizacion,Aca.Fecha_Registro)) Fecha_Actualizacion
                    from acad_curso_alumno aca inner join acad_carga_curso acc on Acc.Id_Carga_Curso=Aca.Id_Carga_Curso
                    inner join Acad_Semestre_Programa asp on Asp.Id_Semestre_Programa=Acc.Id_Semestre_Programa
                    group by aca.id_persona,aca.Id_Plan_Programa,asp.id_semestre) fa on fa.id_persona=aac.id_persona
                            and fa.Id_Plan_Programa=aac.Id_Plan_Programa and fa.id_semestre=aac.id_semestre
  WHERE AAC.ID_MODO_CONTRATO=1 AND AAC.ESTADO='1' AND SUBSTR(AAC.SEMESTRE,-2) IN ('-1','-2')
  AND APE.ID_ESCUELA=p_escuela AND AAC.ID_PERSONA=V_ID_PERSONA AND AAC.SEMESTRE>=V_SEMESTRE;
  
  RETURN p_semestre;

END;

