SET client_encoding TO 'UTF8';

SET search_path = david,public;
\set ON_ERROR_STOP ON

FUNCTION FT_PRIMERA_MATRICULA_ALUMNO( V_ID_PERSONA IN NUMBER, V_ID_PLAN_PROGRAMA IN NUMBER 
) 
RETURN VARCHAR2 
IS 
p_escuela integer;
p_semestre varchar2(500);
BEGIN

  SELECT ID_ESCUELA INTO p_escuela FROM VW_PLAN_PROGRAMA WHERE ID_PLAN_PROGRAMA=V_ID_PLAN_PROGRAMA;

  SELECT MIN(AAC.SEMESTRE) SEMESTRE INTO p_semestre
  FROM VW_ACAD_ALUMNO_CONTRATO AAC INNER JOIN VW_ACAD_PROGRAMA_ESTUDIO APE ON APE.ID_PROGRAMA_ESTUDIO=AAC.ID_PROGRAMA_ESTUDIO
  WHERE AAC.ID_MODO_CONTRATO=1 AND AAC.ESTADO='1' AND SUBSTR(AAC.SEMESTRE,-2) IN ('-1','-2')
  AND APE.ID_ESCUELA=p_escuela AND ID_PERSONA=V_ID_PERSONA;
  
  RETURN p_semestre;

END;

