\set ON_ERROR_STOP ON

function ft_profesor_principal(v_id_carga_curso in integer)

RETURN varchar2 IS

	p_nombre	  varchar2(2000);

	
BEGIN

	select p.paterno||' '||p.materno||' '||p.nombre into p_nombre
	    FROM ACAD_CARGA_CURSO_DOCENTE ccd INNER JOIN 
	    MOISES.Persona p ON p.ID_PERSONA =ccd.ID_PERSONA  
    WHERE ccd.ID_CARGA_CURSO = v_id_carga_curso AND ccd.ID_TIPO_DOCENTE =1 AND rownum=1;
       

return p_nombre;

END;

