SET client_encoding TO 'UTF8';

SET search_path = david,public;
\set ON_ERROR_STOP ON

function ft_profesor_teoria(v_id_carga_curso in integer)
RETURN varchar2 IS
	p_nombre	  varchar2(2000);
		   
BEGIN

	select p.paterno||' '||p.materno||' '||p.nombre into p_nombre
  from MOISES.Persona p inner join (select Accd.Id_Persona 
                                    from Acad_Carga_Curso_Docente accd inner join Tipo_Horario th on Accd.Id_Tipo_Horario=Th.Id_Tipo_Horario and Th.Codigo like 'T%'
                                    and accd.Id_Carga_Curso=v_id_carga_curso
                                    order by Th.Codigo) d on P.Id_Persona=D.Id_Persona and rownum=1;
       
return p_nombre;

END;

