SET client_encoding TO 'UTF8';

SET search_path = david,public;
\set ON_ERROR_STOP ON

function             ft_residencia(p_id_reserva_residencia in VARCHAR2) 

return VARCHAR2

is

  res VARCHAR2(100);

  

begin  

	

	SELECT residencia 

	INTO res 

	FROM (


	SELECT nvl(ar.nombre,'Sin residencia') AS residencia FROM david.ACAD_RESERVA_RESIDENCIA arr 
	INNER JOIN david.ACAD_RESIDENCIA ar ON ar.ID_RESIDENCIA =arr.ID_RESIDENCIA 
	WHERE arr.ID_RESERVA_RESIDENCIA =p_id_reserva_residencia
	)

	WHERE rownum=1

	;



	

  return res;

  

end;

