FUNCTION ft_resultado_aprendizaje(
    p_id_carga_unidad number 
) 

RETURN varchar2
IS
    r_descripcion varchar2(4000) ;
BEGIN

SELECT 
nvl(accra.DESCRIPCION ,ra.DESCRIPCCION )  INTO r_descripcion
FROM ACAD_CARGA_UNIDAD_RA accra 
left JOIN RESULTADO_APRENDIZAJE ra ON ra.ID_RESULTADO_APRENDIZAJE = accra.ID_RESULTADO_APRENDIZAJE 
WHERE accra.ID_CARGA_UNIDAD  = p_id_carga_unidad
AND rownum=1;

    RETURN r_descripcion;
END;

