SET client_encoding TO 'UTF8';

SET search_path = david,public;
\set ON_ERROR_STOP ON

FUNCTION ft_semestre_ant( v_semestre in varchar2) 
RETURN varchar2 IS
	p_semestre	  varchar2(800);
		   
BEGIN

	select (case when substr(v_semestre,-1) ='2' then substr(v_semestre,0,4)||'-1'
          else substr(v_semestre,0,4)-1||'-2'
          end ) into p_semestre
  from dual;
       
return p_semestre;

END;

