FUNCTION ft_tarea(
    p_id_carga_unidad number 
) 

RETURN varchar2
IS
    r_descripcion varchar2(4000) ;
BEGIN

SELECT 
acct.DESCRIPCION INTO r_descripcion
FROM 
ACAD_CARGA_CURSO_TAREA acct  
INNER JOIN ACAD_CARGA_UNIDAD acu ON acct.ID_CARGA_UNIDAD =acu.ID_CARGA_UNIDAD 
WHERE acct.ID_CARGA_UNIDAD  = p_id_carga_unidad
AND rownum=1;

    RETURN r_descripcion;
END;

