SET client_encoding TO 'UTF8';

SET search_path = david,public;
\set ON_ERROR_STOP ON

function ft_tercio_nom_curso(v_id_semestre number, v_id_persona number, v_id_sede number, v_nom_curso varchar2)
return number
is
    p_puesto number;
    
begin

    p_puesto:=0;
    
    select min(puesto) into p_puesto from (
    select rownum puesto,id_persona
    from (select id_persona,Promedio
          from Vw_Acad_Curso_Alumno 
          where id_semestre=v_id_semestre and Nombre_Curso=v_nom_curso
          and id_sede=v_id_sede
          order by Promedio desc))
    where id_persona=v_id_persona and puesto Between 1 and 3;
    
    return p_puesto;

end;

