function ft_tiene_desbloqueo(v_id_persona integer,v_id_plan_programa integer,v_id_semestre integer)
return number
is
  p_res number;
begin

  select nvl((select min( decode(Aca.Id_Tipo_Condicion,2,0,7,1) ) res
              from Vw_Acad_Curso_Alumno aca
              inner join Acad_Semestre asm on Asm.Id_Semestre_Ant=aca.id_semestre
              inner join Vw_Persona_Alumno pa on pa.id_persona=aca.id_persona
              inner join Aatemp_Desbloqueo_Requisito dr on Dr.Codigo=Pa.Codigo and dr.semestre= asm.id_semestre
              where asm.id_semestre=v_id_semestre and aca.estado='1' and Aca.Id_Nivel_Ensenanza=1 and Aca.Id_Tipo_Condicion in (7,2)
              and Aca.Id_Persona=v_id_persona and Aca.Id_Plan_Programa=v_id_plan_programa
              group by aca.id_persona),0) into p_res 
  from dual;
  
  return p_res;

end;

