SET client_encoding TO 'UTF8';

SET search_path = david,public;
\set ON_ERROR_STOP ON

function ft_tiene_regular(v_id_persona integer,v_id_plan_programa integer)
return number
is
  p_res number;
  p_ciclo number;
begin

  select nvl((select max(Ciclo)
              from vw_acad_alumno_contrato where id_persona=v_id_persona and Id_Modo_Contrato=1 
              and Id_Plan_Programa=v_id_plan_programa and estado='1'),0) into p_ciclo 
  from dual;
  
  select decode((select count(*)
                      from acad_plan_programa app inner join vw_acad_curso_plan acpe on Acpe.Id_Plan=app.id_plan
                      where app.id_plan_programa=v_id_plan_programa and acpe.ciclo<=p_ciclo and Acpe.Id_Plan_Curso_Electivo is null
                      and not Exists (select 1
                                      from acad_curso_alumno aca inner join acad_plan_curso acp on Acp.Id_Plan_Curso=Aca.Id_Plan_Curso
                                      where aca.id_persona=v_id_persona and aca.Id_Plan_Programa=app.id_plan_programa and acp.ciclo=acpe.ciclo and Aca.Id_Tipo_Condicion in (1,13,17)
                                      and decode(Acp.Id_Plan_Curso_Electivo,null,Acp.Id_Plan_Curso,Acp.Id_Plan_Curso_Electivo)=Acpe.Id_Plan_Curso)),0,0,1) into p_res
  from dual;
  
  return p_res;    
      
end;

