function ft_tipo_persona(v_id_persona integer,v_semestre in varchar2) 
return varchar2
is
  p_tipo varchar2(500);
begin  
  
  select tipo into p_tipo
  from (
        select distinct 1 dato,'Trabajador' tipo from ENOC.Vw_Trabajador where id_persona=v_id_persona
        union
        select distinct 2 dato,'Egresado' 
        from moises.persona_natural_egresado pne inner join Vw_Plan_Programa vpp on Vpp.Id_Plan_Programa=Pne.Id_Plan_Programa
        where id_persona=v_id_persona and Vpp.Id_Nivel_Ensenanza=1
        union
        select distinct 3 dato, 'Ingresante' from Vw_Alumno_Plan_Programa a 
        where id_persona=v_id_persona and Id_Tipo_Contrato in (1,2,4,5) and estado='1' and substr(Nombre_Plan,0,4)>=2017 and estado='1'
        and not Exists (select 1 from Vw_Acad_Alumno_Contrato b 
                        where b.id_persona=a.id_persona and B.Id_Plan_Programa=A.Id_Plan_Programa
                        and estado='1' and substr(semestre,0,4)>=2017)
        union
        select distinct 4 dato, 'Estudiante' from Vw_Acad_Alumno_Contrato where id_persona=v_id_persona and semestre=v_semestre and estado='1'
        union
        select distinct 5 dato, 'Posgrado' 
        from moises.persona_natural_egresado pne inner join Vw_Plan_Programa vpp on Vpp.Id_Plan_Programa=Pne.Id_Plan_Programa
        where id_persona=v_id_persona and Vpp.Id_Tipo_Contrato in (4,5)
        union
        select distinct 5 dato, 'Posgrado' from Vw_Acad_Alumno_Contrato 
        where id_persona=v_id_persona and Id_Tipo_Contrato in (4,5) and semestre=v_semestre and estado='1'
        order by 1)
  where rownum=1;
  
  return p_tipo;

end;

