SET client_encoding TO 'UTF8';

SET search_path = david,public;
\set ON_ERROR_STOP ON

function ft_tipo_rub_eval(p_id_carga_curso integer,p_tipo in varchar2) 
return number
is
  p_resultado number;

begin
    
    if(p_tipo='T') then
    
      select count(*) into p_resultado
      from acad_carga_evaluacion ace
      where Ace.Id_Carga_Curso=p_id_carga_curso;
      
    end if;
    
    if(p_tipo='V') then
    
      select count(*) into p_resultado
      from acad_carga_evaluacion ace
      where ace.Fecha_Evaluacion<sysdate
      and not Exists (select distinct 's' 
                      from Acad_Alumno_Evaluacion aae
                      where Aae.Id_Carga_Evaluacion=Ace.Id_Carga_Evaluacion)
      and Ace.Id_Carga_Curso=p_id_carga_curso;
      
    end if;
    
    if(p_tipo='E') then
    
      select count(*) into p_resultado
      from acad_carga_evaluacion ace
      where Exists (select distinct 's' 
                      from Acad_Alumno_Evaluacion aae
                      where Aae.Id_Carga_Evaluacion=Ace.Id_Carga_Evaluacion)
      and Ace.Id_Carga_Curso=p_id_carga_curso;
      
    end if;
  
  return p_resultado;

end;

