FUNCTION is_date( p_fecha IN date )
  RETURN NUMBER
IS
  l_num NUMBER;
BEGIN

  l_num := to_number( (case when to_char(p_fecha,'yyyy')>2100 then to_char(to_number(to_char(p_fecha,'yyyy'))-1000) 
        else (case when to_char(p_fecha,'yyyy')>to_char(sysdate,'yyyy') then to_char(to_number(to_char(p_fecha,'yyyy'))-100) 
                    else  to_char(p_fecha,'yyyy') end) end) );
  /*select (to_date(to_char(p_fecha,'dd/mm')||'/'||
  (case when to_char(p_fecha,'yyyy')>2100 then to_char(to_number(to_char(p_fecha,'yyyy'))-1000) 
        else (case when to_char(p_fecha,'yyyy')>to_char(sysdate,'yyyy') then to_char(to_number(to_char(p_fecha,'yyyy'))-100) 
                    else  to_char(p_fecha,'yyyy') end) end))) into l_num
    from dual;*/
  
  RETURN l_num;
  
EXCEPTION
  WHEN value_error
  THEN
    RETURN 0;
END;

