procedure ft_agregar_egresado_semestre(p_id_semestre in integer)
 is
  
  v_num integer :=0;
    
 begin 
 
    insert into MOISES.Persona_Natural_Egresado(ID_PERSONA,ID_PLAN_PROGRAMA,ANIO,ID_SEMESTRE,GRADUACION,TITULO)
    select distinct ID_PERSONA,ID_PLAN_PROGRAMA,substr(semestre,0,4) ANIO,ID_SEMESTRE,0 GRADUACION,0 TITULO 
    from Vw_Acad_Alumno_Contrato aac
    where Aac.Id_Semestre=p_id_semestre and estado='1' and Aac.Id_Modo_Contrato=1 and Aac.Id_Nivel_Ensenanza in (1,2)
    and ft_graduado_plan(aac.id_persona,aac.id_plan_programa)=0
    and (ID_PERSONA,ID_PLAN_PROGRAMA) not in (select ID_PERSONA,ID_PLAN_PROGRAMA from MOISES.Persona_Natural_Egresado);
    
    commit;

 end;

