procedure ft_eliminar_contrato_24h_PL(p_id_semestre in integer)
 is
  
  cursor contrato is
  select aac.id_alumno_contrato
  from vw_acad_alumno_contrato aac inner join acad_semestre asm on asm.id_semestre=aac.id_semestre and asm.estado='1'
  where Aac.Id_Modo_Contrato=1 and aac.estado in ('0','M') 
  AND (nvl(Aac.Fecha_Actualizacion,Aac.Fecha_Registro)+2)<sysdate
  and id_programa_estudio=4
  and aac.id_semestre=p_id_semestre
  ;
    
begin 
 
    for cc in contrato loop
      pcd_anular_contrato_alumno_n(cc.id_alumno_contrato);
    end loop;

 end;

