procedure       ft_eliminar_contrato_72h_LIM
 is
  
  cursor contrato is
  select aac.id_alumno_contrato
  from vw_acad_alumno_contrato aac inner join acad_semestre asm on asm.id_semestre=aac.id_semestre and asm.estado='1'
  where Aac.Id_Modo_Contrato<>4 and aac.estado in ('0','M') and Aac.Id_Sede in (1/*,2,3*/
) and Aac.Id_Tipo_Contrato=1
  --and Id_escuela not in (221,220,223,224,222,112)
  and Id_Programa_Estudio in (3,21,142144,145,718,146,147,149,150,151,720,152,153,155,156,157,159,346,143,719,148,154,17,738,19)
  AND (nvl(Aac.Fecha_Actualizacion,sysdate)+(case when id_programa_estudio in (3,21,142144,145,718,146,147,149,150,151,720,152,153,155,156,157,159,346,143,719,148,154,17,738,19) then 3 else 2 end))<sysdate
  and aac.id_semestre=225
  ;
    
 begin 
 
    for cc in contrato loop
      pcd_anular_contrato_alumno_n(cc.id_alumno_contrato);
    end loop;

 end;

