procedure ft_eliminar_contrato_72h
 is
  
  cursor contrato is
  select aac.id_alumno_contrato
  from vw_acad_alumno_contrato aac inner join acad_semestre asm on asm.id_semestre=aac.id_semestre and asm.estado='1'
  where Aac.Id_Modo_Contrato<>4 and aac.estado in ('0','M')
  AND (nvl(Aac.Fecha_Actualizacion,sysdate)+3)<sysdate
  --and aac.id_semestre=107
  ;
    
 begin 
 
    for cc in contrato loop
      pcd_anular_contrato_alumno_n(cc.id_alumno_contrato);
    end loop;

 end;

