procedure ft_eliminar_contrato_ci_tpp
 is
    cursor contrato is
    select vaac.id_alumno_contrato
    from vw_acad_alumno_contrato vaac inner join vw_acad_programa_estudio vape on vape.id_programa_estudio = vaac.id_programa_estudio
    where vaac.id_modo_contrato <> 4
    and vaac.estado in ('0','M')
    and vaac.id_sede = 3
    and vaac.id_tipo_contrato = 16
    and vape.id_facultad = 72
    --and vaac.id_programa_estudio in (412)
    and (nvl(vaac.fecha_actualizacion,sysdate)+2) < sysdate  --48h
    and vaac.id_semestre = 231;

 begin 

    for cc in contrato loop
      pcd_anular_contrato_alumno_n(cc.id_alumno_contrato);
    end loop;

 end;

