procedure ft_eliminar_cont_proceso_all(p_id_semestre in integer)
 is
  
  cursor contrato is
  select distinct id_alumno_contrato
  from vw_acad_alumno_contrato
  where id_semestre=p_id_semestre and Id_Modo_Contrato=1 
  and Id_nivel_ensenanza in (1,2) and estado in ('0','M')
  and id_alumno_contrato not in (
  select distinct aac.id_alumno_contrato--,aac.estado,nvl(Aca.Promedio,0),Aca.Id_Tipo_Condicion,aca.estado
  from vw_acad_alumno_contrato aac inner join Acad_Alumno_Contrato_Curso aacc on Aacc.Id_Alumno_Contrato=Aac.Id_Alumno_Contrato
  inner join acad_curso_alumno aca on Aca.Id_Curso_Alumno=Aacc.Id_Curso_Alumno
  where aac.id_semestre=p_id_semestre and aac.Id_Modo_Contrato=1 
  and aac.Id_nivel_ensenanza in (1,2) and aac.estado in ('0','M') and Aca.estado='1');
 
begin 
 
    for cc in contrato loop
      pcd_anular_contrato_alumno_n(cc.id_alumno_contrato);
    end loop;

 end;

