procedure       ft_eliminar_procesoyconfir(p_id_semestre in integer,p_id_nivel_ensenanza in integer,p_id_modo_contrato in integer)
 is
  
  cursor contrato is
  select aac.id_alumno_contrato
  from vw_acad_alumno_contrato aac
  where Aac.Id_Modo_Contrato=p_id_modo_contrato and aac.estado in ('0','M')
  AND aac.id_nivel_ensenanza=p_id_nivel_ensenanza
  and aac.id_semestre=p_id_semestre
  ;
    
 begin 
 
    for cc in contrato loop
      pcd_anular_contrato_alumno_n(cc.id_alumno_contrato);
    end loop;

 end;

