procedure ft_eliminar_variacion_1h
 is
  
  cursor contrato is
  select id_alumno_contrato
  from Vw_Acad_Alumno_Contrato where Id_Modo_Contrato=4
  and Id_Semestre=199 and estado<>'1' and Fecha_Registro<sysdate-(1/24)
  order by id_alumno_contrato;
    
 begin 
 
    for cc in contrato loop
      pcd_anular_variacion_alumno(cc.id_alumno_contrato);
    end loop;

 end;

