procedure pcd_create_reportes(v_id_persona integer) is

begin

  insert into aatemp_reportar
  select Aac.Codigo,Aac.Paterno||' '||Aac.Materno||' '||nombre as estudiante, Aac.Sede,Aac.Nombre_Facultad,Aac.Nombre_Escuela, 
  nvl(ft_calcular_ciclo_programa('2022-1',aac.id_persona,aac.id_plan_programa)+1,1) Ciclo, Aac.Celular,Aac.Correo,'NO' Ingresante,
  nvl((select distinct 'MATRICULADO' from Vw_Acad_Alumno_Contrato where id_persona=aac.id_persona and id_plan_programa=Aac.Id_Plan_Programa and id_semestre=181 and estado='1' and id_modo_contrato=1),'NO MATRICULADO') estado,
  ft_deuda_alumno(aac.id_persona,2022) deuda
  from Vw_Acad_Alumno_Contrato aac
  where id_semestre=175 and estado='1' and id_modo_contrato=1 and Aac.Id_Nivel_Ensenanza=1
  union
  select Pa.Codigo,Pa.Apellido_Nombre,App.Sede,App.Nom_Facultad,App.Nom_Escuela,
  nvl(ft_calcular_ciclo_programa('2022-1',pa.id_persona,app.id_plan_programa)+1,1),Pa.Celular,Pa.Correo,'SI' ingresante, 
  nvl((select distinct 'MATRICULADO' from Vw_Acad_Alumno_Contrato where id_persona=pa.id_persona and id_plan_programa=app.Id_Plan_Programa and id_semestre=181 and estado='1' and id_modo_contrato=1),'NO MATRICULADO') estado,
  ft_deuda_alumno(pa.id_persona,2022) deuda
  from Vw_Alumno_Plan_Programa app 
  inner join vw_persona_alumno pa on Pa.Id_Persona=App.Id_Persona 
  where App.Nombre_Plan like '2022%';
  
  commit;

end;

