SET client_encoding TO 'UTF8';

SET search_path = david,public;
\set ON_ERROR_STOP ON

procedure pcd_reg_candado_ingles_act(v_activo integer)
is

  cursor semestre is
  select id_semestre
  from acad_semestre
  where estado='1' and substr(semestre,-2) in ('-1','-2','-0','-M') and substr(semestre,0,4)=to_char(sysdate,'YYYY')
  ;
  
  begin
  
    for cc in semestre loop
      pcd_reg_candado_ingles_sem(cc.id_semestre);
    end loop;
    
  commit;
  end;

