procedure       pcd_update_riesgo_alumno(val integer) is

v_id_semestre integer;

begin 

SELECT MAX(ID_SEMESTRE) into v_id_semestre
FROM DAVID.ACAD_SEMESTRE WHERE ESTADO_ADVISER=1;


 pcd_alumno_ciclo(v_id_semestre);
  commit;
  
  delete aatemp_alumno_contrato_beca where id_semestre=v_id_semestre;
  commit;
  
  insert into aatemp_alumno_contrato_beca
  select Distinct *
  from  (select id_persona,(select id_tipo_beca_estatal from acad_alumno_beca where id_persona=aac.id_persona and id_tipo_requisito_beca=33) id_tipo_beca_estatal,id_semestre
          from vw_acad_alumno_contrato aac where tipo_alumno='B18' and id_semestre=v_id_semestre)
  where (id_persona,id_tipo_beca_estatal,id_semestre) not in (select id_persona,id_tipo_beca_estatal,id_semestre from aatemp_alumno_contrato_beca)
  and id_tipo_beca_estatal is not null;
  commit;
  
  delete aatemp_nivel_riesgo_alumno where id_semestre=v_id_semestre;
  commit;
  
  insert into aatemp_nivel_riesgo_alumno
  select distinct asp.id_semestre,Ape.Id_Programa_Estudio,
  Ft_Calcular_Ciclo(asm.semestre,aac.id_persona,Aac.Id_Nivel_Ensenanza) ciclo,
  nvl(Ft_Calcular_grupo(asm.semestre,aac.id_persona,Aac.Id_Nivel_Ensenanza),'Ãšnico') grupo,
  aac.id_persona,ft_ponderado_ciclo(aac.id_persona,asp.id_semestre,Aac.Id_Nivel_Ensenanza) ponderado,
  nvl(ft_cant_estado_cursos_ciclo(aac.id_persona, Aac.Id_Nivel_Ensenanza, Aac.Id_Plan_Programa, Asp.Id_Semestre, 'CNA'),0) CNA,
  nvl(ft_cant_estado_cursos_ciclo(aac.id_persona, Aac.Id_Nivel_Ensenanza, Aac.Id_Plan_Programa, Asp.Id_Semestre, 'CRA'),0) CRA,
  nvl(ft_cant_estado_cursos_ciclo(aac.id_persona, Aac.Id_Nivel_Ensenanza, Aac.Id_Plan_Programa, Asp.Id_Semestre, 'CD'),0) CD,
  nvl(ft_cant_estado_cursos_ciclo(aac.id_persona, Aac.Id_Nivel_Ensenanza, Aac.Id_Plan_Programa, Asp.Id_Semestre, 'TC'),0) TC,
  nvl(ft_cant_estado_cursos_ciclo(aac.id_persona, Aac.Id_Nivel_Ensenanza, Aac.Id_Plan_Programa, Asp.Id_Semestre, 'SD'),0) SD,
  nvl(ft_cant_estado_cursos_ciclo(aac.id_persona, Aac.Id_Nivel_Ensenanza, Aac.Id_Plan_Programa, Asp.Id_Semestre, 'SA'),0) SA,
  nvl(ft_cant_estado_cursos_ciclo(aac.id_persona, Aac.Id_Nivel_Ensenanza, Aac.Id_Plan_Programa, Asp.Id_Semestre, 'TS'),0) TS,
  ft_nivel_riesgo(ft_ponderado_ciclo(aac.id_persona,asp.id_semestre,Aac.Id_Nivel_Ensenanza), 
                  nvl(ft_cant_estado_cursos_ciclo(aac.id_persona, Aac.Id_Nivel_Ensenanza, Aac.Id_Plan_Programa, Asp.Id_Semestre, 'SD'),0), 'C') riesgo
  from acad_alumno_contrato aac
  inner join acad_semestre_programa asp on Asp.Id_Semestre_Programa=Aac.Id_Semestre_Programa and aac.estado in ('1')
  and asp.id_semestre in (v_id_semestre)
  inner join acad_semestre asm on asm.id_semestre=Asp.Id_Semestre
  inner join vw_acad_programa_estudio ape on Ape.Id_Programa_Estudio=Asp.Id_Programa_Estudio
  inner join Acad_Matricula_Detalle amd on Amd.Id_Matricula_Detalle=Aac.Id_Matricula_Detalle and Amd.Id_Modo_Contrato=1
  where Ape.Id_Tipo_Contrato in (1,2);
  COMMIT;

END;

