                 INNER JOIN DAVID.ACAD_SEMESTRE e ON d.ID_SEMESTRE = e.ID_SEMESTRE
        WHERE b.ID_CARGA_TEMA = :NEW.ID_CARGA_TEMA;

        SELECT SEMESTER || '-' || :NEW.ID_CARGA_TEMA || '-' || SQ_ACAD_PLAN_CLASE_RES_CODIGO.NEXTVAL INTO :NEW.CODIGO
        FROM dual;
    END IF;
END
