DROP TRIGGER IF EXISTS trig_residencia_permiso_codigo ON residencia_permiso CASCADE;
CREATE OR REPLACE FUNCTION david.trigger_fct_trig_residencia_permiso_codigo() RETURNS trigger AS $BODY$
DECLARE
    CODIGO_RESID RESIDENCIA_TIPO_HABITACION.CODIGO%TYPE;
BEGIN
    IF :NEW.CODIGO IS NULL THEN

        SELECT NVL(RTH.CODIGO, '')
        INTO CODIGO_RESID
        FROM VW_ACAD_ALUMNO_CONTRATO VAAC
                 LEFT JOIN RESIDENCIA_TIPO_HABITACION RTH
                           ON VAAC.ID_RESID_TIPO_HABITACION = RTH.ID_RESID_TIPO_HABITACION
        WHERE VAAC.ID_ALUMNO_CONTRATO = :NEW.ID_ALUMNO_CONTRATO
          AND VAAC.ID_RESID_TIPO_HABITACION IS NOT NULL
          AND VAAC.ID_MODO_CONTRATO = 1
          AND VAAC.ESTADO = '1';

        SELECT CODIGO_RESID || '-' || TO_CHAR(SQ_RESIDENCIA_PERMISO_CODIGO.NEXTVAL, 'FM000000000000000')
        INTO :NEW.CODIGO
        FROM dual;
    END IF;
END
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
