DROP TRIGGER IF EXISTS trig_residencia_permiso_codigo ON residencia_permiso;
CREATE TRIGGER trig_residencia_permiso_codigo
	BEFORE INSERT ON residencia_permiso FOR EACH ROW
	EXECUTE PROCEDURE david.trigger_fct_trig_residencia_permiso_codigo();

