BEGIN

    IF :new.ID_ALUM_FOTO_TEMP IS NULL THEN
        SELECT DAVID.SQ_TRIG_TR_ALUM_FOTO_TEMP_ID.NEXTVAL
        INTO :new.ID_ALUM_FOTO_TEMP
