CREATE OR REPLACE FUNCTION david.trigger_fct_trig_tr_persona_firma_id() RETURNS trigger AS $BODY$
BEGIN
    IF :new.ID_PERSONA_FIRMA IS NULL THEN
        SELECT DAVID.SQ_TR_PERSONA_FIRMA_ID.NEXTVAL
        INTO :new.ID_PERSONA_FIRMA
