        SELECT SUBSTR(TA.NOMBRE, 1, 3) || '-' || TO_CHAR(SYSDATE, 'YYYY') || '-' ||
               TO_CHAR(:new.ID_PERSONA_FIRMA, 'FM00000') INTO :NEW.CODIGO
