SET client_encoding TO 'UTF8';

SET search_path = david,public;
\set ON_ERROR_STOP ON

DROP TRIGGER IF EXISTS trig_tr_tramite_persona ON tr_tramite_persona CASCADE;
CREATE OR REPLACE FUNCTION david.trigger_fct_trig_tr_tramite_persona() RETURNS trigger AS $BODY$
DECLARE
    P_CODIGO           VARCHAR2(100) := '';
    P_SIGLA            VARCHAR2(100) := '';
    P_ID_PROCEDIMIENTO NUMBER(10)    := NULL;

BEGIN

    IF :NEW.ID_ANHO IS NULL THEN
        :NEW.ID_ANHO := TO_CHAR(SYSDATE, 'YYYY');
    END IF;

    IF :NEW.FECHA_REGISTRO IS NULL THEN
        :NEW.FECHA_REGISTRO := SYSDATE;
    END IF;

    IF :NEW.FECHA_IMPRIME IS NULL THEN
        :NEW.FECHA_IMPRIME := SYSDATE;
    END IF;


    IF :NEW.ID_TIPO_TRAMITE IS NOT NULL THEN
        SELECT TTT.ID_ENTIDAD,
               TTT.ID_DEPTO,
               TTT.ID_AREA,
               TTT.CODIGO,
               TTT.NOMBRE,
               TTT.DEPTO,
               TTT.AREA,
               UPPER(OS.SIGLA) AS SIGLA,
               TTT.ID_PROCEDIMIENTO
        INTO
            :NEW.ID_ENTIDAD,
            :NEW.ID_DEPTO,
            :NEW.ID_AREA,
            P_CODIGO,
            :NEW.TIPO_TRAMITE,
            :NEW.DEPTO,
            :NEW.AREA,
            P_SIGLA,
            P_ID_PROCEDIMIENTO
        FROM DAVID.TR_TIPO_TRAMITE TTT
                 INNER JOIN ELISEO.ORG_SEDE OS ON TTT.ID_DEPTO = OS.ID_DEPTO
        WHERE TTT.ID_TIPO_TRAMITE = :NEW.ID_TIPO_TRAMITE;
    END IF;


    -- Documento de identidad
    IF :NEW.ID_PERSONA IS NOT NULL AND :NEW.NUM_DOCUMENTO IS NULL AND :NEW.TIPO_DOCUMENTO IS NULL THEN
        BEGIN
            SELECT DOC.TIPO_DOCUMENTO, DOC.NUM_DOCUMENTO
            INTO :NEW.TIPO_DOCUMENTO,
                :NEW.NUM_DOCUMENTO
            FROM (SELECT PD.NUM_DOCUMENTO, NVL(TD.SIGLAS, TD2.SIGLAS) AS TIPO_DOCUMENTO
                  FROM MOISES.PERSONA_DOCUMENTO PD
                           INNER JOIN MOISES.PERSONA_NATURAL PN ON PD.ID_PERSONA = PN.ID_PERSONA
                           LEFT JOIN MOISES.TIPO_DOCUMENTO TD ON PD.ID_TIPODOCUMENTO = TD.ID_TIPODOCUMENTO
                           LEFT JOIN MOISES.TIPO_DOCUMENTO TD2 ON PN.ID_TIPODOCUMENTO = TD2.ID_TIPODOCUMENTO
                  WHERE PD.ID_PERSONA = :NEW.ID_PERSONA
                    AND PD.ES_ACTIVO = 1
                  ORDER BY PD.NUM_DOCUMENTO DESC) DOC
            WHERE ROWNUM <= 1;
        EXCEPTION
            WHEN NO_DATA_FOUND THEN
                :NEW.TIPO_DOCUMENTO := NULL;
                :NEW.NUM_DOCUMENTO := NULL;
        END;
    END IF;

    -- CORREO
    IF :NEW.ID_PERSONA IS NOT NULL AND :NEW.CORREO IS NULL THEN

        BEGIN
            SELECT COR.DIRECCION
            INTO :NEW.CORREO
            FROM (SELECT PV.DIRECCION
                  FROM MOISES.PERSONA_VIRTUAL PV
                  WHERE PV.ID_PERSONA = :NEW.ID_PERSONA
                    AND PV.ES_ACTIVO = 1
                  ORDER BY PV.ID_VIRTUAL DESC) COR
            WHERE ROWNUM <= 1;
        EXCEPTION
            WHEN NO_DATA_FOUND THEN
                :NEW.CORREO := NULL;
        END;
    END IF;

    -- Telefono
    IF :NEW.ID_PERSONA IS NOT NULL AND :NEW.TELEFONO IS NULL THEN

        BEGIN
            SELECT RET.NUM_TELEFONO
            INTO :NEW.TELEFONO
            FROM (SELECT PT.NUM_TELEFONO
                  FROM MOISES.PERSONA_TELEFONO PT
                  WHERE PT.ID_PERSONA = :NEW.ID_PERSONA
                    AND PT.ES_ACTIVO = 1
                  ORDER BY PT.ID_TELEFONO DESC) RET
            WHERE ROWNUM <= 1;
        EXCEPTION
            WHEN NO_DATA_FOUND THEN
                :NEW.TELEFONO := NULL;
        END;
    END IF;

    -- Datos personales
    IF :NEW.ID_PERSONA IS NOT NULL THEN

        -- InformaciÃ³n como estudiante
        BEGIN
            SELECT NVL(:NEW.NOMBRE, UPPER(P.NOMBRE))   AS NOMBRE,
                   NVL(:NEW.PATERNO, UPPER(P.PATERNO)) AS PATERNO,
                   NVL(:NEW.MATERNO, UPPER(P.MATERNO)) AS MATERNO,
                   PNA.CODIGO
            INTO
                :NEW.NOMBRE,
                :NEW.PATERNO,
                :NEW.MATERNO,
                :NEW.CODIGO_ALUMNO
            FROM MOISES.PERSONA P
                     INNER JOIN MOISES.PERSONA_NATURAL_ALUMNO PNA ON P.ID_PERSONA = PNA.ID_PERSONA
            WHERE P.ID_PERSONA = :NEW.ID_PERSONA;

        EXCEPTION
            WHEN NO_DATA_FOUND THEN
                :NEW.NOMBRE := NULL;
                :NEW.PATERNO := NULL;
                :NEW.MATERNO := NULL;
                :NEW.CODIGO_ALUMNO := NULL;
        END;


        -- Campus al cual pertenece
        BEGIN

            SELECT DEP.ID_DEPTO
            INTO :NEW.ID_DEPTO_ALUMNO
            FROM (SELECT B.ID_DEPTO
                  FROM ELISEO.CONTA_ENTIDAD_USUARIO A,
                       ELISEO.LAMB_USERS_DEPTO B
                           JOIN
                       ELISEO.ORG_SEDE C ON (B.ID_DEPTO = C.ID_DEPTO)
                  WHERE A.ID_ENTIDAD = B.ID_ENTIDAD
                    AND A.ID_PERSONA = B.ID
                    AND A.ID_PERSONA = :NEW.ID_PERSONA
                    AND A.ESTADO = 1
                    AND B.ESTADO = 1
                  ORDER BY B.ID_DEPTO) DEP
            WHERE ROWNUM <= 1;
        EXCEPTION
            WHEN NO_DATA_FOUND THEN
                :NEW.ID_DEPTO_ALUMNO := NULL;
        END;


    END IF;


    IF :NEW.ID_TRAMITE_PERSONA IS NULL THEN
        SELECT DAVID.SQ_TR_TRAMITE_PERSONA_ID.NEXTVAL INTO :NEW.ID_TRAMITE_PERSONA FROM DUAL;

        IF :NEW.ID_ESTADO IS NULL THEN

            IF P_ID_PROCEDIMIENTO IS NOT NULL THEN
                SELECT TE.ID_ESTADO INTO :NEW.ID_ESTADO FROM DAVID.TR_ESTADO TE WHERE TE.CODIGO = 'TR_AUTO';
            ELSE
                SELECT TE.ID_ESTADO INTO :NEW.ID_ESTADO FROM DAVID.TR_ESTADO TE WHERE TE.CODIGO = 'TR_APER';
            END IF;

        END IF;

    END IF;


    IF :NEW.CODIGO IS NULL THEN
        SELECT ('TR' || '-' || P_CODIGO || P_SIGLA || TO_CHAR(SYSDATE, 'YYYY') || :NEW.NUM_DOCUMENTO ||
                :NEW.ID_TRAMITE_PERSONA)
        INTO :NEW.CODIGO
        FROM DUAL;
    END IF;


END
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION david.trigger_fct_trig_tr_tramite_persona() FROM PUBLIC;

DROP TRIGGER IF EXISTS trig_tr_tramite_persona ON tr_tramite_persona;
CREATE TRIGGER trig_tr_tramite_persona
	BEFORE INSERT ON tr_tramite_persona FOR EACH ROW
	EXECUTE PROCEDURE david.trigger_fct_trig_tr_tramite_persona();

