CREATE OR REPLACE VIEW vw_adviser_derivacion_estado (id_programa_estudio, nombre, id_adviser_derivacion, derivacion, id, valor) AS SELECT ape.ID_PROGRAMA_ESTUDIO,ape.nombre,ad.ID_ADVISER_DERIVACION,ad.nombre AS derivacion ,c.id , c.valor 
FROM ACAD_PROGRAMA_ESTUDIO ape ,
ADVISER_DERIVACION ad , (
	SELECT 1 AS id ,'Enviado' AS valor 
	FROM dual UNION SELECT 2,'Visto'
	FROM dual UNION SELECT 3,'Atendido' 
	FROM dual  
)c
ORDER BY 1,2,3;

