CREATE OR REPLACE VIEW vw_alumno_cursos_equiv_aprob (id_persona, id_curso_detalle) AS select Distinct ce.id_persona,acd.Id_Curso_Detalle  
FROM acad_curso_detalle acd
inner join (select nvl(aca.Id_Curso_Detalle,0) Id_Curso_Detalle,nvl(Vce.Curso_Equiv,0) Curso_Equiv,aca.id_persona 
            from acad_curso_alumno aca left join (select ace.id_curso_detalle,aed.id_curso_detalle curso_equiv
                                                  from acad_curso_equivalencia ace inner join acad_equivalencia_detalle aed on aed.Id_Curso_Equivalencia=ace.Id_Curso_Equivalencia) vce 
                                       on Vce.Id_Curso_Detalle=aca.Id_Curso_Detalle
            where aca.Id_Tipo_Condicion=1) ce on (acd.Id_Curso_Detalle=ce.Id_Curso_Detalle or acd.Id_Curso_Detalle=ce.curso_equiv)
--where ce.id_persona=14777
;

