CREATE OR REPLACE VIEW vw_responsavel_div (id_resp, id_aluno, id_escola, nome, tpdocumento, documento, sexo, tpafiliacao, isresponsavelfinanceiro, isresponsavelacademico, anio) AS select Distinct p.Id_Persona id_Resp,
arf.id_persona id_aluno,
arf.Id_Facultad id_Escola,
p.Paterno||' '||p.Materno||' '||p.Nombre Nome,
td.Siglas tpDocumento, Pn.Num_Documento Documento,
Ts.Nombre sexo, trf.nombre tpAfiliacao,
1 isResponsavelFinanceiro,1 isResponsavelAcademico,arf.anio
FROM MOISES.Persona_Natural_Resp_Fin rf
inner join moises.persona p on p.id_persona=rf.id_persona
inner join moises.persona_natural pn on pn.id_persona=p.id_persona
left join MOISES.Tipo_Documento td on td.Id_Tipodocumento=Pn.Id_Tipodocumento
left join MOISES.Tipo_Sexo ts on Ts.Sexo=pn.sexo
inner join (select id_persona,Id_Facultad,Id_Resp_Financiero,Id_Tipo_Resp_Financiero,Substr(semestre,0,4) anio
            from Vw_Acad_Alumno_Contrato) arf on arf.Id_Resp_Financiero=rf.Id_Persona
inner join TIPO_RESP_FINANCIERO trf on Trf.Id_Tipo_Resp_Financiero=arf.Id_Tipo_Resp_Financiero
order by arf.anio;

